import { useState } from 'react';
import { useEmploiContext } from "../../hooks/useEmploiContext";
import { EmploiList } from "../../data/emploi";
import formatDistanceToNow from 'date-fns/formatDistanceToNow';
import "./EmploiDetails.css"; // Add your CSS file

const EmploiDetail = ({ emploi }) => {
  const { dispatch } = useEmploiContext();
  const [showDetails, setShowDetails] = useState(false); // State to show/hide details
  const [showEmail, setShowEmail] = useState(false); // State to show/hide email popup

  // Function to handle delete
  const handleClick = async () => {
    const response = await fetch('/api/offreEmploi/' + emploi._id, {
      method: 'DELETE',
    });

    const json = await response.json();

    if (response.ok) {
      console.log('Suppression rÃ©ussie:', json);
      dispatch({ type: 'DELETE_EMPLOIS', payload: json });
    }
  };

  // Function to toggle job details
  const toggleDetails = () => {
    setShowDetails(prevState => !prevState);
  };

  // Function to toggle email visibility
  const toggleEmail = () => {
    setShowEmail(prevState => !prevState);
  };

  return (
    <div className="emploi-detail-container">
      <ul className="lmj-emploi-list">
        <div className="emploi-container">
          <h3 className="jobTitle" onClick={toggleDetails} style={{ cursor: "pointer" }}>
            {emploi.nom_poste}
          </h3>
          <span className="jobEntreprise">
            <span className="label">Entreprise:</span> {emploi.nom_entreprise}
          </span>
          <span className="jobLocation">
            <span className="label">Emplacement:</span> {emploi.emplacement}
          </span>
          <button className="buttonP" onClick={toggleEmail}>
            Afficher l'email
          </button>

          {/* Email popup */}
          {showEmail && (
            <div className="popup">
              <div className="popup-content">
                <span className="close" onClick={toggleEmail}>&times;</span>
                <h4>Email: <a href={`mailto:${emploi.email_employeur}`}>{emploi.email_employeur}</a></h4>
              </div>
            </div>
          )}

          {/* Details popup */}
          {showDetails && (
            <div className="details-popup">
              <div className="details-popup-content">
                <span className="close" onClick={toggleDetails}>&times;</span>
                <div className="details-content">
                  <h4>Description:</h4>
                  <p>{emploi.description}</p>
                  <h4>ResponsabilitÃ©s:</h4>
                  <p>{emploi.responsabilites}</p>
                  <h4>Exigences:</h4>
                  <p>{emploi.exigences}</p>
                </div>
              </div>
            </div>
          )}

          <button onClick={handleClick} className="delete-button">Supprimer l'annonce</button>
        </div>
      </ul>
    </div>
  );
};

export default EmploiDetail;
