import React, { useState } from "react";
import "./Inscription.css";
import NavBar from '../NavBar/NavBar';
import { useNavigate } from 'react-router-dom';
import { useCandidatContext } from "../../hooks/useCandidatContext";

 // Pour naviguer après la soumission




const Inscription = () => {
  const {dispatch} = useCandidatContext()

  const [nomCandidat,setNomCandidat] = useState('')
  const [emailCandidat, setEmailCandidat] = useState('')
  const [mot_de_passeCandidat, setMDPcandidat] = useState('')


  const[error, setError] = useState(null)

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(nomCandidat, emailCandidat, mot_de_passeCandidat);

    const candidat = {
      nom_candidat: nomCandidat,
      email_candidat: emailCandidat,
      mot_de_passeCandidat: mot_de_passeCandidat
    };

    try {
      const response = await fetch('/api/candidat/', {
        method: 'POST',
        body: JSON.stringify(candidat),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorText = await response.text(); // Récupère le texte de l'erreur
        throw new Error(errorText); // Lève une erreur avec le message d'erreur
      }

      const json = await response.json();
      setNomCandidat('');
      setEmailCandidat('');
      setMDPcandidat('');
      setError(null);
      console.log('compte entreprise Ajouter mon amour');
      dispatch({ type: 'SIGNUPCANDIDAT', payload: json });
    } catch (err) {
      setError(err.message);
      console.error("Erreur lors de l'inscription:", err);
    }
  };

return (
  <div>
    <NavBar />
    <div className="inscription-container">
      <h2>Inscription candidat</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label>Nom du candidat</label>
          <input
            type="text"
            value={nomCandidat}
            onChange={(e) => setNomCandidat(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>Email</label>
          <input
            type="email"
            value={emailCandidat}
            onChange={(e) => setEmailCandidat(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>Mot de passe</label>
          <input
            type="password"
            value={mot_de_passeCandidat}
            onChange={(e) => setMDPcandidat(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="submit-btn">S'inscrire</button>
      </form>
      {error && <p className="error">{error}</p>}
    </div>
  </div>
);
};

export default Inscription;









