import React from 'react';
import { Link } from 'react-router-dom';
import './NavBar.css'; 
import Logo from '../../images/logo.jpg';
import { FaHeart } from 'react-icons/fa';

// Importation des hooks de contexte et de déconnexion pour les candidats et entreprises
import { useCandidatLougout } from '../../hooks/useCandidatLogout';
import { useEntrepriseLougout } from '../../hooks/useEntrepriseLogout';
import { useCandidatContext } from '../../hooks/useCandidatContext';
import { useEntrepriseContext } from '../../hooks/useEntrepriseContext';

const NavBar = () => {
  const { candidat } = useCandidatContext();
  const { entreprise } = useEntrepriseContext();

  const { lougoutcandidat } = useCandidatLougout();
  const { lougoutentreprise } = useEntrepriseLougout();

  const menuOptions = [
    { text: "Connexion", path: "/con" },
    { text: "Entreprise", path: "/conent" },
  ];

  return (
    <nav>
      <div className="nav-container">
        <div className="nav-logo-and-link">
          <img src={Logo} alt="Logo" />
          <Link to="/" className="navbar-link navbar-link-bold">Accueil</Link>
          <Link to="/like" className="heart2-iconn">
            <FaHeart color="white" size={25} />
          </Link>
        </div>

        <div className="navbar-links-container">
          {menuOptions.map((item, index) => (
            <Link to={item.path} className="navbar-link" key={index}>{item.text}</Link>
          ))}
        </div>

        {/* Vérification et affichage conditionnel des informations de connexion */}
        {candidat ? (
          <div className="user-info">
            <span>{candidat.email_candidat}</span>
            <button onClick={lougoutcandidat} className="logout-button">
              Log out Candidat
            </button>
          </div>
        ) : (
          <p style={{ color: 'red' }}>Candidat non connecté</p>
        )}

        {entreprise ? (
          <div className="user-info">
            <span>{entreprise.email_entreprise}</span>
            <button onClick={lougoutentreprise} className="logoutEnt-button">
              Log out Entreprise
            </button>
          </div>
        ) : (
          <p style={{ color: 'red' }}>Entreprise non connectée</p>
        )}
      </div>
    </nav>
  );
};

export default NavBar;
