import React, { useState } from "react";
import "./ConnexionEnt.css";
import NavBar from '../NavBar/NavBar';
import { useNavigate } from 'react-router-dom';
import { useConnexionEntreprise } from "../../hooks/useConnexionEntreprise";

const ConnexionEnt = () => {
  const navigate = useNavigate();
  const [emailEntreprise, setEmailEntreprise] = useState('');
  const [mot_de_passeEntreprise, setMDPentreprise] = useState('');
  const { connexionEnt, error, isLoading } = useConnexionEntreprise();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const success = await connexionEnt(emailEntreprise, mot_de_passeEntreprise);

    if (success) {
      navigate('/ent'); // Redirige vers la page entreprise après la connexion
    } else {
      console.log('Erreur de connexion');
    }
  };

  return (
    <div>
      <NavBar />
      <div className="connexion-container">
        <h2>Connexion</h2>
        <p>Employeur</p>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label>Email</label>
            <input
              type="email"
              value={emailEntreprise}
              onChange={(e) => setEmailEntreprise(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <label>Mot de passe</label>
            <input
              type="password"
              value={mot_de_passeEntreprise}
              onChange={(e) => setMDPentreprise(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="submit-btn" disabled={isLoading}>Se connecter</button>
        </form>

        <div className="signup">
          <p>Pas de compte avec nous ?</p>
          <br />
          <a href="./inscent">
            <button disabled={isLoading} className="signup-btn">Créer un compte</button>
            {error && <div>{error}</div>}
          </a>
        </div>
      </div>
    </div>
  );
}

export default ConnexionEnt;
