import { useState } from 'react';
import './EmploiDetails.css';
import edit from '../../images/edit.png';

const EmploiDetail = ({ emploi }) => {
    const [showDetails, setShowDetails] = useState(false);
    const [showEmail, setShowEmail] = useState(false);
    const [showEditForm, setShowEditForm] = useState(false);
    const [nomPoste, setNomPoste] = useState(emploi.nom_poste);
    const [nomEntreprise, setNomEntreprise] = useState(emploi.nom_entreprise);
    const [salaire, setSalaire] = useState(emploi.salaire);
    const [emplacement, setEmplacement] = useState(emploi.emplacement);
    const [categorie, setCategorie] = useState(emploi.categorie);
    const [emailEmployeur, setEmailEmployeur] = useState(emploi.email_employeur);
    const [description, setDescription] = useState(emploi.description);
    const [responsabilite, setResponsabilite] = useState(emploi.responsabilite);
    const [exigence, setExigence] = useState(emploi.exigence);
    const [error, setError] = useState(null);

    const toggleDetails = () => {
        setShowDetails(prevState => !prevState);
    };

    const toggleEmail = () => {
        setShowEmail(prevState => !prevState);
    };

    const handleEditClick = () => {
        setShowEditForm(true);
    };

    const handleCancelEdit = () => {
        setShowEditForm(false);
    };

    return (
        <div className="emploi-detail-container">
            <ul className="lmj-emploi-list">
                <div className="emploi-container"> 
                    <div className="image-container" onClick={handleEditClick}>
                        <img src={edit} alt="edit" className="small-image" />
                    </div>
                    <h3 className="jobTitle" onClick={toggleDetails} style={{ cursor: "pointer" }}>
                        {nomPoste}
                    </h3>
                    <span className="jobEntreprise">
                        <span className="label">Entreprise:</span> {nomEntreprise}
                    </span>
                    <span className="jobLocation">
                        <span className="label">Emplacement:</span> {emplacement}
                    </span>
                    <span className="jobSalary">
                        <span className="label">Salaire:</span> {salaire}
                    </span>
                    <span className="jobSector">
                        <span className="label">Catégorie:</span> {categorie}
                    </span>
                    <button className="buttonAf" onClick={toggleEmail}>
                        Afficher l'email
                    </button>

                    {showEmail && (
                        <div className="popup email-popup">
                            <div className="popup-content">
                                <span className="close" onClick={toggleEmail}>&times;</span>
                                <h4>Email: <a href={`mailto:${emailEmployeur}`}>{emailEmployeur}</a></h4>
                            </div>
                        </div>
                    )}

                    {showDetails && (
                        <div className="details-popup">
                            <div className="details-popup-content">
                                <span className="close" onClick={toggleDetails}>&times;</span>
                                <div className="details-content">
                                    <h4>Description:</h4>
                                    <p>{description}</p>
                                    <h4>Responsabilités:</h4>
                                    <p>{responsabilite}</p>
                                    <h4>Exigences:</h4>
                                    <p>{exigence}</p>
                                </div>
                            </div>
                        </div>
                    )}

                    {error && <div className="error-message">{error}</div>}
                    <button onClick={handleClick} className="delete-button" disabled={loading}>
            {loading ? 'Suppression...' : 'Supprimer l\'annonce'}
          </button>
                </div>
            </ul>

            {showEditForm && (
                <div className="edit-form-container">
                    <h2>Modifier l'offre d'emploi</h2>
                    <form onSubmit={(e) => e.preventDefault()}>
                        <div className="input-group">
                            <label>Nom de l'entreprise:</label>
                            <input 
                                type="text" 
                                value={nomEntreprise} 
                                onChange={(e) => setNomEntreprise(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Nom du poste:</label>
                            <input 
                                type="text" 
                                value={nomPoste} 
                                onChange={(e) => setNomPoste(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Salaire:</label>
                            <input 
                                type="text" 
                                value={salaire} 
                                onChange={(e) => setSalaire(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Emplacement:</label>
                            <input 
                                type="text" 
                                value={emplacement} 
                                onChange={(e) => setEmplacement(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Catégorie:</label>
                            <input 
                                type="text" 
                                value={categorie} 
                                onChange={(e) => setCategorie(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Email de l'employeur:</label>
                            <input 
                                type="email" 
                                value={emailEmployeur} 
                                onChange={(e) => setEmailEmployeur(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Description:</label>
                            <textarea 
                                value={description} 
                                onChange={(e) => setDescription(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Responsabilités:</label>
                            <textarea 
                                value={responsabilite} 
                                onChange={(e) => setResponsabilite(e.target.value)} 
                            />
                        </div>
                        <div className="input-group">
                            <label>Exigences:</label>
                            <textarea 
                                value={exigence} 
                                onChange={(e) => setExigence(e.target.value)} 
                            />
                        </div>
                        <button type="submit">Enregistrer les modifications</button>
                        <button type="button" onClick={handleCancelEdit}>Annuler</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default EmploiDetail;
